  addEmployee(val:any){
    return this.http.post(`${this.url}`,val)
  }

  updateEmployee(val:any,id:any){
      return this.http.put(`${this.url}/${id}`, val)
  }

  deleteEmployee(id:any){
    return this.http.delete(`${this.url}/${id}`)
  }
}
